import './charList.scss';
import abyss from '../../resources/img/abyss.jpg';
import { Component } from 'react';
import MarvelService from '../../services/MarvelService';

class CharList extends Component {
    state = {
        characters: [], // Используем массив для хранения списка персонажей
        loading: true,  // Состояние загрузки
        error: false    // Состояние ошибки
    }

    marvelService = new MarvelService();

    componentDidMount() {
        this.updateCharList(); // Загружаем персонажей при монтировании
    }

    onCharListLoaded = (characters) => {
        this.setState({
            characters,
            loading: false,
            error: false
        });
    }

    onError = () => {
        this.setState({
            loading: false,
            error: true
        });
    }

    updateCharList = () => {
        this.marvelService
            .getAllCharacters()  // Загружаем список персонажей
            .then(this.onCharListLoaded)
            .catch(this.onError);
    }

    render() {
        const { characters, loading, error } = this.state;

        if (loading) {
            return <div>Loading...</div>; // Показываем сообщение о загрузке
        }

        if (error) {
            return <div>Error loading characters</div>; // Показываем сообщение об ошибке
        }

        return (
            <div className="char__list">
                <ul className="char__grid">
                    {characters.map((char) => (
                        <li className="char__item" key={char.id}>
                            <img src={char.thumbnail} alt={char.name} />
                            <div className="char__name">{char.name}</div>
                        </li>
                    ))}
                    {/* Пример статического элемента */}
                    <li className="char__item char__item_selected">
                        <img src={abyss} alt="abyss" />
                        <div className="char__name">Abyss</div>
                    </li>
                </ul>
                <button className="button button__main button__long">
                    <div className="inner">load more</div>
                </button>
            </div>
        );
    }
}

export default CharList;
